mapper.select:

SELECT COUNT(*)
FROM ARTICLE
WHERE AR_GENRE = #{ar_genre}

mapper.update:
UPDATE ARTICLE
SET AR_HITS = AR_HITS + 1
WHERE AR_NO = #{ar_no}

mapper.select:
SELECT AR_HITS
FROM ARTICLE
WHERE AR_NO = #{ar_no}

SELECT *
FROM ARTICLE
WHERE AR_NO = #{ar_no}

SELECT COUNT(*)
FROM DUP_CHECK
WHERE DUP_AR_NO=#{ar_no}
	AND DUP_MID = #{mid}

mapper.update:
UPDATE ARTICLE
SET AR_THUMBSUP = AR_THUMBSUP+1
WHERE AR_NO = #{ar_no}

UPDATE ARTICLE
SET AR_THUMBSDOWN = AR_THUMBSDOWN-1
WHERE AR_NO = #{ar_no}

mapper.select:
SELECT COUNT(*)
FROM COMMENTS
WHERE CM_AR_NO = #{ar_no}

SELECT *
FROM COMMENTS
WHERE CM_AR_NO = #{ar_no}

SELECT CM_AR_NO
FROM COMMENTS
WHERE CM_NO = #{cm_no}

mapper.update:
UPDATE COMMENTS
SET CM_DETAIL=#{cm_detail}, CM_DATE=SYSDATE
WHERE CM_NO = #{cm_no}
